import React from "react";
import { FaGithub } from "react-icons/fa";

const menus = [
  { name: "소개", href: "#hero" },
  { name: "설치", href: "#install" },
  { name: "사용법", href: "#usage" },
  { name: "규칙 안내", href: "#rules" },
];

const versions = ["v1.0", "v1.1", "v1.2"];

export default function Header() {
  const [dark, setDark] = React.useState(false);
  const [version, setVersion] = React.useState(versions[0]);
  return (
    <header className="fixed top-0 left-0 w-full z-30 bg-white dark:bg-gray-900 shadow-md transition-colors">
      <div className="max-w-7xl mx-auto flex items-center justify-between px-4 py-2">
        {/* Logo */}
        <a href="#hero" className="font-extrabold text-xl text-blue-600 tracking-tight select-none">
          Turtle Watch
        </a>
        {/* Menu */}
        <nav className="hidden md:flex gap-8 ml-8">
          {menus.map((menu) => (
            <a
              key={menu.name}
              href={menu.href}
              className="text-gray-700 dark:text-gray-200 hover:text-blue-500 dark:hover:text-blue-400 font-medium transition-colors"
            >
              {menu.name}
            </a>
          ))}
        </nav>
        {/* Right icons */}
        <div className="flex items-center gap-4">
          {/* GitHub */}
          <a
            href="https://github.com/your-org/turtle-watch"
            target="_blank"
            rel="noopener noreferrer"
            className="text-gray-600 dark:text-gray-300 hover:text-black dark:hover:text-white text-xl"
            aria-label="GitHub"
          >
            <FaGithub />
          </a>
          {/* Version Dropdown */}
          <select
            className="bg-transparent border-none text-gray-700 dark:text-gray-200 font-semibold focus:outline-none cursor-pointer"
            value={version}
            onChange={(e) => setVersion(e.target.value)}
            aria-label="버전 선택"
          >
            {versions.map((v) => (
              <option key={v} value={v} className="text-black dark:text-white">
                {v}
              </option>
            ))}
          </select>
          {/* Dark mode toggle */}
          <button
            onClick={() => {
              setDark((d) => !d);
              if (typeof window !== "undefined") {
                document.documentElement.classList.toggle("dark");
              }
            }}
            className="ml-2 text-lg text-gray-600 dark:text-gray-300 hover:text-blue-500 dark:hover:text-blue-400 transition-colors"
            aria-label="다크모드 토글"
          >
            {dark ? (
              <span role="img" aria-label="라이트 모드">🌞</span>
            ) : (
              <span role="img" aria-label="다크 모드">🌙</span>
            )}
          </button>
        </div>
      </div>
    </header>
  );
}
