import React from 'react';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { tomorrow } from 'react-syntax-highlighter/dist/esm/styles/prism';
import TableOfContents from './components/TableOfContents';
import Header from './components/Header';

const App: React.FC = () => {
  const gradleCode = `dependencies {
    implementation 'io.github.turtle-watch:turtle-watch:1.0.0'
}`;

  const ymlCode = `turtle-watch:
  enabled: true
  threshold-ms: 500  # 500ms 이상 걸리는 쿼리를 슬로우 쿼리로 감지
  log-level: WARN
  show-stack-trace: true
  analyze-execution-plan: true`;

  const handleSectionClick = (sectionId: string) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <Header />
      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 pt-28">
        <div className="flex gap-8">
          {/* Table of Contents */}
          {/* <div className="hidden lg:block">
            <TableOfContents onSectionClick={handleSectionClick} />
          </div> */}
          {/* Content */}
          <div className="flex-1">
            {/* Docs(소개) */}
            <section id="hero" className="mb-12">
              <h2 className="text-3xl font-bold text-gray-900 mb-6">Turtle Watch란?</h2>
              <p className="text-lg text-gray-600 mb-8">
                Turtle Watch는 데이터베이스 쿼리의 실행 계획을 자동으로 분석하고 성능 문제를 진단하는 Java 라이브러리입니다.
              </p>
              {/* 설치/설정/주요기능 등은 이 섹션에 포함 */}
              {/* ... 기존 설치, 설정, 주요 기능, 규칙 안내 등 내용 ... */}
            </section>
            {/* Release Notes */}
            <section id="release-notes" className="mb-12">
              <h2 className="text-3xl font-bold text-gray-900 mb-6">Release Notes</h2>
              <div className="space-y-6">
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">v1.2.0</h3>
                  <ul className="list-disc list-inside text-gray-600">
                    <li>실행 계획 규칙 진단 기능 추가</li>
                    <li>설정 옵션 개선</li>
                  </ul>
                </div>
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">v1.1.0</h3>
                  <ul className="list-disc list-inside text-gray-600">
                    <li>슬로우 쿼리 탐지 기능 추가</li>
                    <li>로그 포맷 개선</li>
                  </ul>
                </div>
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">v1.0.0</h3>
                  <ul className="list-disc list-inside text-gray-600">
                    <li>초기 릴리즈: 실행 계획 분석, 기본 진단 기능 제공</li>
                  </ul>
                </div>
              </div>
            </section>
          </div>
        </div>
      </div>
      {/* Footer */}
      <footer className="bg-white border-t border-gray-200 mt-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <p className="text-center text-gray-500 text-sm">
            © 2024 Turtle Watch. All rights reserved.
          </p>
        </div>
      </footer>
    </div>
  );
};

export default App;
